            backgroundSize: "cover",
            backgroundPosition: "50%",
            margin: "8px 25px",
            border: "5px solid #f1f1f1"
        }
    }

    render() {
        return <div className="col-xl-2 col-lg-2 col-md-3 col-sm-4 col-xs-4"
        style={this.styles.photo}
        role="img"
        onClick={this.shuffleArray}>
    </div>
    }

};

export default Photo;
