class Photo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: false,
            photo: {
                width: 170,
                height: 170,
                cursor: "pointer",
                backgroundImage: "url("+this.props.url+")",
                backgroundSize: "cover",
                backgroundPosition: "50%",
                margin: "8px 25px",
                border: "5px solid #f1f1f1"
            }
        }
    };

    componentDidUpdate = (prevProps) => {
        console.log(prevProps);
        console.log(this.props);
        if (prevProps.url !== this.props.url) {
            this.mapStateToProps();
        }
    }

    mapStateToProps = () => {
        let photo = this.state.photo;
        photo.backgroundImage = "url("+this.props.url+")";
        this.setState({ photo });
    }

    render() {
        console.log(this.state.photo);
        return <div className="col-xl-2 col-lg-2 col-md-3 col-sm-4 col-xs-4"
        // style={{...this.state.photo,backgroundImage:"url("+this.props.url+")"}}
        style={this.state.photo}
        role="img"
        onClick={this.props.shuffleArray}>
    </div>
    }

};

export default Photo;
